import { useEffect, useState } from 'react'

function useDebounce(value: string, delay = 300) {
  const [inputValue, setInputValue] = useState(value)
  useEffect(() => {

    setTimeout(() => {
      setInputValue(value)
    }, delay)

  }, [value, delay])

  return inputValue

}
